<?php
 namespace Zend\Uri; class Http extends Uri { protected static $validSchemes = array('http', 'https'); protected static $defaultPorts = array( 'http' => 80, 'https' => 443, ); protected $validHostTypes = self::HOST_DNSORIPV4; protected $user; protected $password; public function isValid() { return parent::isValid(); } public function getUser() { if (null !== $this->user) { return $this->user; } $this->parseUserInfo(); return $this->user; } public function getPassword() { if (null !== $this->password) { return $this->password; } $this->parseUserInfo(); return $this->password; } public function setUser($user) { $this->user = $user; return $this; } public function setPassword($password) { $this->password = $password; return $this; } public static function validateHost($host, $allowed = self::HOST_DNSORIPV4) { return parent::validateHost($host, $allowed); } protected function parseUserInfo() { if (null === $this->userInfo) { return; } if (false === strpos($this->userInfo, ':')) { $this->setUser($this->userInfo); return; } list($user, $password) = explode(':', $this->userInfo, 2); $this->setUser($user); $this->setPassword($password); } } 